const express = require('express');
const axios = require('axios');
require('dotenv').config();

const app = express();
const port = 3000;

// Middleware
app.use(express.json());

// Routes
app.post('/register', async (req, res) => {
  try {
    const response = await axios.post(`${process.env.API_BASE_URL}/auth/register`, req.body);
    res.json(response.data);
  } catch (error) {
    res.status(500).json({ error: error.message });
  }
});

app.post('/buy', async (req, res) => {
  try {
    const response = await axios.post(`${process.env.API_BASE_URL}/buy`, req.body);
    res.json(response.data);
  } catch (error) {
    res.status(500).json({ error: error.message });
  }
});

// Example endpoint using Lideflow API
const lideflowBaseUrl = 'https://lideflow.com/api'; // Adjust this URL based on Lideflow API docs

app.post('/transfer', async (req, res) => {
  try {
    const { to, amount } = req.body;
    const response = await axios.post(`${lideflowBaseUrl}/transfer`, {
      to,
      amount,
      // Add any other required parameters as per Lideflow API docs
    });
    res.json(response.data);
  } catch (error) {
    res.status(500).json({ error: error.message });
  }
});

// Other routes (sell, collect, etc.) can be added similarly

app.listen(port, () => {
  console.log(`Server running at http://localhost:${port}`);
});
